<?php
$translations = array();
$blogs = get_sites();
foreach($blogs as $blog) {
	$path = strtoupper(str_replace('/', '', $blog -> path));
  $path = $path == '' ? 'EN' : $path;
	$translation_id = get_post_meta( get_queried_object_id(), 'translation_'.$path, true );
	if (get_current_blog_id()==$blog -> blog_id) continue;
	if ($translation_id == 0) continue;
	switch_to_blog($blog -> blog_id);
	$translations[] = array(
		'href' => get_permalink($translation_id),
		'class' => 'flag '.$path,
		'path' => $path
	);
	restore_current_blog();
}
if (!empty($translations)) {
	echo '<div class="translations">';
	foreach ($translations as $translation) {
		echo '<a href="'.$translation['href'].'" class="'.$translation['class'].'" title="View this page translated in '.$translation['path'].'"></a>';
	}
	echo '</div>';
}
?>

